        "address": {
          "address1": "{{$randomStreetAddress}}",
          "city": "{{$randomCity}}",
          "countryCode": "USA",
          "postalCode": "92626",
          "subCountryCode": "CA"
        },
        "person": {
          "firstName": "{{$randomFirstName}}",
          "formattedName": "{{$randomFullName}}",
          "formalSalutation": "Mr",
          "informalSalutation": "Mr",
          "middleName": "J",
          "lastName": "{{$randomLastName}}",
          "namePrefix": {
            "code": "MR",
            "description": "Mister"
          }
        },
        "cardType": { "code": "VISA" }
      }
    ]
