fragment TaxonomyTagHeadlineStack on TaxonomyTag {
  slug
  type
}

fragment TeamTagHeadlineStack on TeamTag {
  data: team {
    id
    teamName
  }
  slug
  type
}

fragment Article_HeadlineStack_Fragment on Article {
  headline
  slug
  tags {
    ...TaxonomyTagHeadlineStack
